import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import type { PersonalSanction } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import jsPDF from 'jspdf';
import autoTable from 'jspdf-autotable';

const SPORTS = [
  'Fútbol', 'Básquetbol', 'Voleibol', 'Rugby', 'Hockey', 'Tenis', 
  'Natación', 'Atletismo', 'Boxeo', 'Karate', 'Judo', 'Taekwondo', 
  'Paddle', 'Golf', 'Ciclismo', 'Gimnasia', 'Handball'
];

function isActive(sanction: PersonalSanction): boolean {
  const today = new Date();
  const endDate = new Date(sanction.fechaFin);
  return endDate >= today;
}
import PersonalSanctionModal from "./PersonalSanctionModal";

interface TribunaSeguraTabProps {
  filters: {
    search: string;
    sport: string;
    status: string;
  };
  onFiltersChange: (filters: any) => void;
}

export default function TribunaSeguraTab({ filters, onFiltersChange }: TribunaSeguraTabProps) {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editingSanction, setEditingSanction] = useState<PersonalSanction | null>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: personalSanctions = [], isLoading, error, isError } = useQuery<PersonalSanction[]>({
    queryKey: ["/api/personal-sanctions"],
    retry: 3,
    retryDelay: 1000,
  });

  if (isError) {
    return (
      <div className="text-center py-12">
        <div className="text-red-600 mb-4">
          <i className="fas fa-exclamation-triangle text-4xl"></i>
        </div>
        <h3 className="text-lg font-semibold text-gray-900 mb-2">Error al cargar las sanciones</h3>
        <p className="text-gray-600 mb-4">No se pudieron cargar los datos. Por favor, intenta nuevamente.</p>
        <button 
          onClick={() => window.location.reload()} 
          className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
        >
          Recargar página
        </button>
      </div>
    );
  }

  // Filter sanctions based on current filters
  const filteredSanctions = personalSanctions.filter((sanction) => {
    const matchesSearch = !filters.search || 
      sanction.nombrePersona.toLowerCase().includes(filters.search.toLowerCase()) ||
      sanction.dniPersona.includes(filters.search) ||
      sanction.deporte.toLowerCase().includes(filters.search.toLowerCase());

    const matchesSport = !filters.sport || sanction.deporte === filters.sport;

    const matchesStatus = !filters.status || 
      (filters.status === 'activa' && isActive(sanction)) ||
      (filters.status === 'vencida' && !isActive(sanction));

    return matchesSearch && matchesSport && matchesStatus;
  });

  const handleFilterChange = (key: string, value: string) => {
    onFiltersChange({ ...filters, [key]: value });
  };

  const clearFilters = () => {
    onFiltersChange({ search: "", sport: "", status: "" });
  };

  // Delete mutation
  const deleteMutation = useMutation({
    mutationFn: (id: string) => apiRequest("DELETE", `/api/personal-sanctions/${id}`, {}),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/personal-sanctions"] });
      toast({
        title: "Sanción eliminada",
        description: "La sanción fue eliminada exitosamente",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "No se pudo eliminar la sanción",
        variant: "destructive",
      });
    },
  });

  const handleDelete = (sanction: PersonalSanction) => {
    if (confirm(`¿Está seguro de eliminar la sanción ${sanction.numeroCarga} de ${sanction.nombrePersona}?`)) {
      deleteMutation.mutate(sanction.id);
    }
  };

  const handleEdit = (sanction: PersonalSanction) => {
    setEditingSanction(sanction);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setEditingSanction(null);
  };

  const generateTribunaSeguraReport = async () => {
    try {
      // Fetch expired unreported sanctions for the monthly report
      const response = await fetch('/api/tribuna-segura-report');
      if (!response.ok) {
        throw new Error('Error al obtener datos del reporte');
      }

      const reportData = await response.json();

      if (reportData.totalSanctions === 0) {
        alert('No hay sanciones vencidas pendientes de reporte para este mes.');
        return;
      }

      const confirmGenerate = confirm(`Reporte Mensual de Tribuna Segura:\\n\\n` +
        `- Personas con sanciones cumplidas: ${reportData.totalSanctions}\\n\\n` +
        `Una vez generado el reporte, estas personas no volverán a aparecer en futuros reportes mensuales.\\n\\n` +
        `El archivo PDF se descargará automáticamente.\\n\\n` +
        `¿Desea generar el reporte PDF?`);

      if (confirmGenerate) {
        // Generate PDF with jsPDF
        console.log("Generando reporte PDF mensual de Tribuna Segura:", reportData);

        const pdf = new jsPDF();

        // Header
        pdf.setFontSize(18);
        pdf.setTextColor(220, 38, 38); // Red color
        pdf.text('SISAE - Sistema Integral de Sanciones y Estadísticas', 20, 20);

        pdf.setFontSize(16);
        pdf.setTextColor(0, 0, 0);
        pdf.text('Reporte Mensual de Tribuna Segura', 20, 35);

        pdf.setFontSize(12);
        pdf.text(`Fecha: ${reportData.date}`, 20, 45);
        pdf.text(`Total de personas con sanciones cumplidas: ${reportData.totalSanctions}`, 20, 55);

        // Table data
        const tableData = reportData.sanctions.map((sanction: any) => [
          `TS-${String(sanction.numeroCarga).padStart(3, '0')}`,
          sanction.nombrePersona,
          sanction.dniPersona,
          `${sanction.edadPersona} años`,
          sanction.deporte,
          sanction.ubicacion,
          sanction.motivoSancion,
          sanction.fechaInicio,
          sanction.fechaFin,
          sanction.observaciones || 'Sin observaciones'
        ]);

        // Add table
        autoTable(pdf, {
          head: [['Nº Carga', 'Nombre', 'DNI', 'Edad', 'Deporte', 'Departamento', 'Motivo', 'Inicio', 'Fin', 'Observaciones']],
          body: tableData,
          startY: 70,
          styles: {
            fontSize: 8,
            cellPadding: 2,
          },
          headStyles: {
            fillColor: [220, 38, 38],
            textColor: 255,
            fontSize: 9,
            fontStyle: 'bold'
          },
          alternateRowStyles: {
            fillColor: [248, 248, 248]
          },
          margin: { left: 10, right: 10 },
        });

        // Footer
        const finalY = (pdf as any).lastAutoTable.finalY + 20;
        pdf.setFontSize(10);
        pdf.setTextColor(100);
        pdf.text('COSEDEPRO Córdoba - Sistema SISAE', 20, finalY);
        pdf.text(`Generado el ${new Date().toLocaleDateString('es-AR')} a las ${new Date().toLocaleTimeString('es-AR')}`, 20, finalY + 10);

        // Download PDF
        const fileName = `Reporte_Tribuna_Segura_${new Date().getFullYear()}_${(new Date().getMonth() + 1).toString().padStart(2, '0')}.pdf`;
        pdf.save(fileName);

        // Mark sanctions as reported
        const sanctionIds = reportData.sanctions.map((s: any) => s.id);
        const markResponse = await fetch('/api/mark-reported', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ sanctionIds })
        });

        if (markResponse.ok) {
          toast({
            title: "Reporte generado y descargado",
            description: `Se reportaron ${reportData.totalSanctions} personas con sanciones cumplidas. El archivo PDF se descargó automáticamente.`,
          });

          // Refresh the data to reflect the changes
          queryClient.invalidateQueries({ queryKey: ["/api/personal-sanctions"] });
        } else {
          throw new Error('Error al marcar sanciones como reportadas');
        }
      }
    } catch (error) {
      console.error('Error generating report:', error);
      toast({
        title: "Error",
        description: "No se pudo generar el reporte mensual",
        variant: "destructive",
      });
    }
  };

  if (isLoading) {
    return (
      <div className="bg-white rounded-lg shadow-md p-6 border border-gray-200">
        <div className="text-center py-8">
          <i className="fas fa-spinner fa-spin text-4xl text-orange-500 mb-4"></i>
          <p className="text-gray-600">Cargando sanciones personales...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Action Buttons */}
      <div className="flex justify-end space-x-4 mb-8">
        <button 
          onClick={() => setIsModalOpen(true)}
          className="gov-button-accent flex items-center space-x-2 py-4 px-8"
          data-testid="button-new-personal-sanction"
        >
          <i className="fas fa-user-plus"></i>
          <span>Nueva Sanción Personal</span>
        </button>
        <button 
          onClick={generateTribunaSeguraReport}
          className="bg-red-700 hover:bg-red-800 text-white font-semibold py-4 px-6 rounded-md transition-all duration-200 shadow-sm hover:shadow-md border border-red-800 flex items-center space-x-2"
          data-testid="button-export-tribuna"
        >
          <i className="fas fa-file-pdf"></i>
          <span>Reporte Mensual PDF</span>
        </button>
      </div>

      {/* Filters */}
      <div className="gov-filter-section">
        <h3 className="gov-section-title">
          <i className="fas fa-filter mr-3" style={{color: 'var(--sisae-yellow)'}}></i>Filtros de Búsqueda
        </h3>
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Buscar</label>
            <input 
              type="text" 
              placeholder="Buscar por nombre, DNI, deporte..." 
              value={filters.search}
              onChange={(e) => handleFilterChange('search', e.target.value)}
              className="gov-input"
              data-testid="input-search-filter"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Deporte</label>
            <select 
              value={filters.sport}
              onChange={(e) => handleFilterChange('sport', e.target.value)}
              className="gov-input"
              data-testid="select-sport-filter"
            >
              <option value="">Todos los deportes</option>
              {SPORTS.map(sport => (
                <option key={sport} value={sport}>{sport}</option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Estado</label>
            <select 
              value={filters.status}
              onChange={(e) => handleFilterChange('status', e.target.value)}
              className="gov-input"
              data-testid="select-status-filter"
            >
              <option value="">Todos</option>
              <option value="activa">Activas</option>
              <option value="vencida">Vencidas</option>
            </select>
          </div>

          <div className="flex items-end">
            <button 
              onClick={clearFilters}
              className="w-full gov-button-secondary py-3 px-4"
              data-testid="button-clear-filters"
            >
              <i className="fas fa-broom mr-2"></i>Limpiar Filtros
            </button>
          </div>
        </div>
      </div>

      {/* Sanctions List */}
      <div className="gov-results-section">
        <div className="flex items-center justify-between mb-6">
          <h3 className="gov-section-title">
            <i className="fas fa-list mr-3" style={{color: 'var(--sisae-yellow)'}}></i>Registro Tribuna Segura - Sanciones Personales
          </h3>
          <div className="bg-gradient-to-r from-yellow-50 to-orange-50 text-yellow-700 px-4 py-2 rounded-md border border-yellow-200">
            <span className="font-semibold" data-testid="results-count">
              {filteredSanctions.length} resultado{filteredSanctions.length !== 1 ? 's' : ''}
            </span>
          </div>
        </div>

        {filteredSanctions.length === 0 ? (
          <div className="bg-gray-50 rounded-lg p-8 text-center border border-gray-200">
            <div className="text-gray-500">
              <i className="fas fa-user-shield text-5xl mb-4"></i>
              <p className="text-xl font-medium mb-2">No hay sanciones personales</p>
              <p className="text-sm">Use los filtros o agregue nuevas sanciones</p>
            </div>
          </div>
        ) : (
          <div className="space-y-4">
            {filteredSanctions.map((sanction) => (
              <div 
                key={sanction.id}
                className={`bg-gradient-to-r ${isActive(sanction) ? 'from-red-50 to-orange-50 border-red-200' : 'from-green-50 to-emerald-50 border-green-200'} rounded-lg shadow-sm p-6 border hover:shadow-md transition-all duration-300`}
                data-testid={`sanction-card-${sanction.id}`}
              >
                <div className="flex justify-between items-start">
                  <div className="flex-1">
                    <div className="flex items-center gap-3 mb-3">
                      <span className="bg-red-600 text-white text-xs font-bold px-2 py-1 rounded">TS-{String((sanction as any).numeroCarga || '---').padStart(3, '0')}</span>
                      <h3 className="text-lg font-bold text-gray-800">{sanction.nombrePersona}</h3>
                      <span className={`px-3 py-1 rounded-full text-sm font-medium ${
                        isActive(sanction) 
                          ? 'bg-red-100 text-red-800 border border-red-200' 
                          : 'bg-green-100 text-green-800 border border-green-200'
                      }`}>
                        {isActive(sanction) ? 'Activa' : 'Vencida'}
                      </span>
                      {(sanction as any).reportadaEnPdf && (
                        <span className="px-2 py-1 bg-blue-100 text-blue-800 text-xs font-medium rounded border border-blue-200">
                          <i className="fas fa-file-pdf mr-1"></i>
                          Reportada
                        </span>
                      )}
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 text-sm">
                      <div>
                        <span className="font-medium text-gray-600">DNI:</span>
                        <p className="text-gray-800">{sanction.dniPersona}</p>
                      </div>
                      <div>
                        <span className="font-medium text-gray-600">Edad:</span>
                        <p className="text-gray-800">{sanction.edadPersona} años</p>
                      </div>
                      <div>
                        <span className="font-medium text-gray-600">Deporte:</span>
                        <p className="text-gray-800">{sanction.deporte}</p>
                      </div>
                      <div>
                        <span className="font-medium text-gray-600">Departamento:</span>
                        <p className="text-gray-800">{(sanction as any).ubicacion || 'No especificado'}</p>
                      </div>
                    </div>

                    <div className="mt-3">
                      <span className="font-medium text-gray-600">Motivo:</span>
                      <p className="text-gray-800 font-medium">{(sanction as any).motivoSancion || 'No especificado'}</p>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm mt-3">
                      <div>
                        <span className="font-medium text-gray-600">Período:</span>
                        <p className="text-gray-800">{sanction.fechaInicio} - {sanction.fechaFin}</p>
                      </div>
                      {sanction.observaciones && (
                        <div>
                          <span className="font-medium text-gray-600">Observaciones:</span>
                          <p className="text-gray-800">{sanction.observaciones}</p>
                        </div>
                      )}
                    </div>
                  </div>

                  {/* Action Buttons */}
                  <div className="flex flex-col gap-2 ml-4">
                    <button
                      onClick={() => handleEdit(sanction)}
                      className="bg-blue-500 hover:bg-blue-600 text-white text-xs font-medium px-3 py-1 rounded transition-colors duration-200 flex items-center gap-1"
                      title="Editar sanción"
                    >
                      <i className="fas fa-edit"></i>
                      Editar
                    </button>
                    <button
                      onClick={() => handleDelete(sanction)}
                      className="bg-red-500 hover:bg-red-600 text-white text-xs font-medium px-3 py-1 rounded transition-colors duration-200 flex items-center gap-1"
                      title="Eliminar sanción"
                      disabled={deleteMutation.isPending}
                    >
                      <i className={`fas ${deleteMutation.isPending ? 'fa-spinner fa-spin' : 'fa-trash'}`}></i>
                      {deleteMutation.isPending ? 'Eliminando...' : 'Eliminar'}
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      <PersonalSanctionModal 
        isOpen={isModalOpen} 
        onClose={handleCloseModal}
        editingSanction={editingSanction}
      />
    </div>
  );
}

