@import url('https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0/css/all.min.css');
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&display=swap');
@tailwind base;
@tailwind components;
@tailwind utilities;

/* Reset básico */
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

:root {
  /* SISAE Icon-based Color Palette */
  --sisae-orange-primary: #FF6B35;
  --sisae-orange-light: #FF7A00;
  --sisae-yellow: #FFB800;
  --sisae-red-alert: #E53935;
  --sisae-gray-dark: #1a1a1a;
  --sisae-gray-medium: #2a2a2a;
  --sisae-gray-light: #4a4a4a;
  --sisae-gray-lighter: #6a6a6a;

  --background: hsl(0, 0%, 98%);
  --foreground: var(--sisae-gray-dark);
  --card: hsl(0, 0%, 100%);
  --card-foreground: var(--sisae-gray-dark);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: var(--sisae-gray-dark);
  --primary: var(--sisae-orange-primary);
  --primary-foreground: hsl(0, 0%, 98%);
  --secondary: var(--sisae-gray-light);
  --secondary-foreground: hsl(0, 0%, 98%);
  --muted: hsl(0, 0%, 96%);
  --muted-foreground: var(--sisae-gray-lighter);
  --accent: var(--sisae-yellow);
  --accent-foreground: var(--sisae-gray-dark);
  --destructive: var(--sisae-red-alert);
  --destructive-foreground: hsl(0, 0%, 98%);
  --border: hsl(0, 0%, 90%);
  --input: hsl(0, 0%, 90%);
  --ring: var(--sisae-orange-primary);
  --radius: 0.5rem;
}

.dark {
  --background: hsl(222.2, 84%, 4.9%);
  --foreground: hsl(210, 40%, 98%);
  --card: hsl(222.2, 84%, 4.9%);
  --card-foreground: hsl(210, 40%, 98%);
  --popover: hsl(222.2, 84%, 4.9%);
  --popover-foreground: hsl(210, 40%, 98%);
  --primary: hsl(217.2, 91.2%, 59.8%);
  --primary-foreground: hsl(222.2, 84%, 4.9%);
